import React, { Component } from 'react'
import testImage from "./test-images/300x200.jpg"
import { test, nativeSort_COL2x, nativeSort_COL, bubbleSort, nativeSort } from "./functions"
import Spinner from 'react-bootstrap/Spinner';
import { Row, Col, Container, Dropdown, DropdownButton } from "react-bootstrap"
import { identifier, isMemberExpression } from '@babel/types'
import { func } from 'prop-types';


export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.imageRef = React.createRef()
        this.canvasRef = React.createRef()
        this.state = {
            currentImageSrc: null,
            imageSelected: false,
            imageLoading: false, 
            imageHeight: null,
            imageWidth: null,
            currentSort: 'test'
        }
    }


    // componentDidMount() {

    // }




    drawNewImage = () => {
        if(!this.state.imageSelected) return alert("please load an image")

        this.setState({
            imageLoading: true
        }, this.resetLoading)
    }

    sortThePixels = () => {
        const algo = this.state.currentSort
        const canvas = this.canvasRef.current
        const ctx = canvas.getContext("2d")
        let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)


        if(algo === 'test') {
            copyData = test(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        if(algo === 'nativeSort_COL_2x') {
            copyData = nativeSort_COL2x(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        if(algo === 'nativeSort_COL') {
            copyData = nativeSort_COL(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        if(algo === 'bubbleSort') {
            copyData = bubbleSort(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        if(algo === 'nativeSort') {
            copyData = nativeSort(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        ctx.putImageData(copyData,0 ,0)
    }


    setSortOption = (e) => {
        this.setState({
            currentSort: e.target.value
        })
    }

    imageLoadHandler = (e) => {
        const canvas = this.canvasRef.current
        const ctx = canvas.getContext("2d")
        const reader = new FileReader();
        const img = new Image();

        reader.onload = function(event){
            img.onload = function(){
                canvas.width = this.width;
                canvas.height = this.height;
                ctx.drawImage(img,0,0);
                }
            img.src = event.target.result;
        }

        reader.readAsDataURL(e.target.files[0]); 

        this.setState({
            currentImageSrc: null,
            imageSelected: true,
            imageLoading: false,
            imageHeight: 1500,      // needs to be set dynamically
            imageWidth: 1000,    // needs to be set dybnamically 
        })
    }

    resetImage = () => {

    }

    test = (e) => {
        const url = URL.createObjectURL(e.target.files[0])
        const img = new Image

        img.onload = function() {
            alert(img.width)
        }




        reader.readAsDataURL(e.target.files[0])

        console.log(img.width)
    }

    render() {

        // const hiddenStyle = {
        //     display: "none"
        // }

        const maxStyle = {
            maxHeight: "700px",
            width: "auto" 
        }

        return (

            <div> 

                <input type="file" accept="image/*" ref={this.uploadedRef} onChange={this.test} >

                </input>

                <select name="options" id="sort-options" onChange={this.setSortOption}>
                    <option value="test">Test</option>
                    <option value="nativeSort_COL_2x">Columned Native Sort x 2</option>
                    <option value="nativeSort_COL">Columned Native Sort</option>
                    <option value="bubbleSort">Bubble Sort</option>
                    <option value="nativeSort">Native Sort</option>
                </select>

                <button onClick={this.sortThePixels}>
                    Sort image
                    {/* {this.checkLoading()} */}
                </button>

                <button onClick={this.resetImage}>
                    Reset image
                </button>


                <div className="container" >

                    {/* <img ref={this.imageRef} src={testImage} />  */}

                        <canvas ref={this.canvasRef} style={maxStyle}/>  

                </div>


            </div>
        )
    }
}


export default CanvasApp
