    constructor(props) {
        super(props)
        this.state = {
             imageHeight: null,
             imageWidth: null
        }
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        canvas.height = 200
        canvas.width = 200
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
          }
        this.setState({
            imageHeight:canvas.height,
            imageWidth:canvas.width
        })  
      }


    drawNewImage = () => {

        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")



        let imageData = ctx.getImageData(0,0, canvas.width, canvas.height)

        for(let i = 0; i < imageData.data.length; n+=4) {
            for(let j = 0; j < imageData.data.length - i - 4; j+=4) {

                let curRed = imageData.data[i]
                let curGreen = imageData.data[i + 1]
                let curBlue = imageData.data[i + 2]
                let curBright = (curRed + curGreen + curBlue) / 3

                let nextRed = imageData.data[i +4] 
                let nextGreen = imageData.data[i + 5]
                let nextBlue = imageData.data[i + 6]
                let nextBright = (nextRed + nextGreen + nextBlue) / 3

                if(curBright < nextBright) {
                    imageData.data[i] = nextRed
                    imageData.data[i+1] = nextGreen
                    imageData.data[i+2] = nextBlue
                    imageData.data[i+4] = curRed
                    imageData.data[i+5] = curGreen
                    imageData.data[i+6] = curBlue
                }

            }
        }

        ctx.putImageData(imageData, 0, 0)




        // ctx.putImageData(copyData,0 ,0 )
    }


