import React, { Component } from 'react'
import testImage from "./test-images/300x200.jpg"
import {  test, nativeSort_COL, bubbleSort, nativeSort } from "./functions"
import Spinner from 'react-bootstrap/Spinner';
import { Row, Col, Container } from "react-bootstrap"
import { identifier } from '@babel/types'


export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.imageRef = React.createRef()
        this.canvasRef = React.createRef()
        this.state = {
            imageSelected: false,
            imageLoading: false, 
            imageHeight: null,
            imageWidth: null,
            currentSort: 'test'
        }
    }


    // componentDidMount() {
    //     const canvas = this.canvasRef.current
    //     const img = this.imageRef.current

    //     const ctx = canvas.getContext("2d")

    //     canvas.height = 200
    //     canvas.width = 300

    //     img.onload = () => {
    //         ctx.drawImage(img, 0, 0)
    //       }

    //     this.setState({
    //         imageHeight:canvas.height,
    //         imageWidth:canvas.width
    //     })  
    //   }




    drawNewImage = () => {
        if(!this.state.imageSelected) return alert("please load an image")

        this.setState({
            imageLoading: true
        }, this.resetLoading)
    }

    sortThePixels = () => {
        const algo = this.state.currentSort
        const canvas = this.canvasRef.current
        const ctx = canvas.getContext("2d")
        let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)

        if(algo === 'nativeSort_COL') {
            copyData = nativeSort_COL(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        if(algo === 'bubbleSort') {
            copyData = bubbleSort(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 
        if(algo === 'nativeSort') {
            copyData = nativeSort(copyData.data, this.state.imageHeight, this.state.imageWidth)
        } 

        ctx.putImageData(copyData,0 ,0 )
    }

    resetLoading = async () => {
        await this.sortThePixels()
    }

    checkLoading = () => {
        if(this.state.imageLoading) {
            return <Spinner animation="grow" size="sm" />
        } else {
            return
        }
    }


    setSortOption = (e) => {
        this.setState({
            currentSort: e.target.value
        })
    }

    imageLoadHandler = (e) => {
        const canvas = this.canvasRef.current
        const ctx = canvas.getContext("2d")
        const reader = new FileReader();
        reader.onload = function(event){
        const img = new Image();
        img.onload = function(){
            canvas.width = img.naturalWidth;
            canvas.height = img.naturalHeight;
            ctx.drawImage(img,0,0);
        }
        img.src = event.target.result;

        }
        reader.readAsDataURL(e.target.files[0]); 
        this.setState({
            imageSelected: true,
            imageLoading: false,
            imageHeight: 1500,      // needs to be set dynamically
            imageWidth: 1000,    // needs to be set dybnamically 
        })
    }


    render() {

        // const hiddenStyle = {
        //     display: "none"
        // }

        const maxStyle = {
            maxHeight: "700px",
            widht: "auto" 
        }

        return (

            <div> 

                <input type="file" onChange={this.imageLoadHandler}>

                </input>

                <select name="options" id="sort-options" onChange={this.setSortOption}>
                    <option value="test">testing</option>
                    <option value="bubbleSort">Bubble Sort</option>
                    <option value="nativeSort">Native Sort</option>
                    <option value="sortByBright">Sort by brightness</option>
                </select>

                <button onClick={this.drawNewImage}>
                    SORT!
                    {/* {this.checkLoading()} */}
                </button>


                <div className="container" >

                    {/* <img ref={this.imageRef} src={testImage} />  */}

                        <canvas ref={this.canvasRef} style={maxStyle}/>  

                </div>


            </div>
        )
    }
}


export default CanvasApp
