import React, { Component } from 'react'
import testImage from "./test-images/150.jpg"
import { arrowFunctionExpression } from '@babel/types'

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.state = {
             imageHeight: null,
             imageWidth: null
        }
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        canvas.height = 200
        canvas.width = 200
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
          }
        this.setState({
            imageHeight:canvas.height,
            imageWidth:canvas.width
        })  
      }



    test = (data) => {
        const rowLength = this.state.imageWidth
        const colLength = this.state.imageHeight 
        let row = []
        let col = []
        let twoDArr = []

        for(let i = 0; i < data.length; i+=4) {
            row = []
            for(let j = 0; j < rowLength; j++) {
                let pixel = [data[i],data[i + 1],data[i + 2],data[i + 3]]
                row.push(pixel)
            }
            twoDArr.push(row)
        }

        console.group('before sort')
        console.log(twoDArr[0])


        for(let i = 0; i < rowLength; i++) {
            for(let j = 0; j < rowLength; j++) {
                if(twoDArr[i][0] < twoDArr[i+1][0]) {
                    twoDArr[i] = twoDArr[i + 1]
                }
            }
        }
        console.log('after sort')
        console.log(twoDArr[0])

    }


    drawNewImage = () => {
        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")
        let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)
        ctx.putImageData(copyData, 0, 0)


        this.test(copyData.data)

        ctx.putImageData(copyData,0 ,0 )
    }


    render() {
        const width = this.state.imageWidth
        const height = this.state.imageHeight


        const hidden = {
            display: "none"
        }

        return (
            <React.Fragment> 

                {/* <div className="container">
                    <img src={testImage} id="img"style={style}/>
                </div> */}

                <button onClick={this.drawNewImage}>
                    test
                </button>

                <div className="container">
                    <canvas ref="canvas"/> 
                    <img ref="image" src={testImage} style={hidden} />
                </div>

                <div> 

                </div>
            </React.Fragment>
        )
    }
}

export default CanvasApp
