import React, { Component } from 'react'
import testImage from "./test-images/200.jpg"
import { arrowFunctionExpression } from '@babel/types'

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.imageRef = React.createRef()
        this.canvasRef = React.createRef()
        this.state = {
             imageHeight: null,
             imageWidth: null
        }

    }

    componentDidMount() {
        const canvas = this.canvasRef.current
        const img = this.imageRef.current
        canvas.height = 200
        canvas.width = 200
        const ctx = canvas.getContext("2d")

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
          }
        this.setState({
            imageHeight:canvas.height,
            imageWidth:canvas.width
        })  
      }



    test = (data) => {

        const rowLength = this.state.imageWidth
        const colLength = this.state.imageHeight 
        let pixelArr = []
        let twoDArr = []

        // Create array of pixels. Each index is a "pixel" array containg the 4 RGBA integers that make up that singular pixel. 

        for(let i = 0; i < data.length; i+=4) {
            let pixel = [data[i],data[i + 1],data[i + 2],data[i + 3]]
            pixelArr.push(pixel)
        }

        // Create rows to be pushed into 2D array. Each row is an array containg the row length of pixel arrays. Do this for the pixel height (column) of the image. 

        for(let i = 0; i < colLength; i++) {

            let row = []                //  init an empty row before each outer loop 

            for(let j = 0; j < rowLength; j++) {
                row.push(pixelArr[j])
            }
            twoDArr.push(row)           // push row into 2D array 
        }

        // console.log(twoDArr[199].slice(0,10))

        //Now that that 2D array has been properly built with row x col dimensions, we can write a pixel sorting algorithm.

        for(let i = 0; i < twoDArr.length; i++) {                   // going over all rows in two d array
            twoDArr[i].sort((a,b) => a[3] - b[3])                   //sorting by [0] index (red hue)
        }

        // console.log(twoDArr[199].slice(0,10))

        // Now build normal array from multi array to be converted to Uint8ClampedArray

        // console.log(twoDArr[0])

        let convertedArr = []

        for(let i = 0; i < twoDArr.length; i++) {
            twoDArr[i].forEach(function(pixel){
                convertedArr.push(pixel[0], pixel[1], pixel[2], pixel[3])
            })
        }

        // create new image object and return 

        const imageData = new ImageData(new Uint8ClampedArray(convertedArr), this.state.imageHeight, this.state.imageWidth)  

        return imageData
    }



    drawNewImage = () => {
        const canvas = this.canvasRef.current
        const ctx = canvas.getContext("2d")
        let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)
        ctx.putImageData(copyData, 0, 0)

        copyData = this.test(copyData.data)

        ctx.putImageData(copyData,0 ,0 )
    }


    render() {

        const convertedStyle = {
            border: "1px solid red"
        }

        return (
            <React.Fragment> 

                <button onClick={this.drawNewImage}>
                    test
                </button>
                <select name="options" id="sort-options" onSelect={this.setOption}>
                    <option value="one">Sort by red</option>
                    <option value="two">Saab</option>
                    <option value="three">Mercedes</option>
                    <option value="four">Audi</option>
                </select>

                <div className="container">

                        <p> Original </p>

                    <img ref={this.imageRef} src={testImage} />

                        <p> to be converted </p>

                    <canvas ref={this.canvasRef}  style={convertedStyle}/> 
                </div>

                <div> 

                </div>
            </React.Fragment>
        )
    }
}


export default CanvasApp
