import React, { Component } from 'react'
import testImage from "./test-images/150.jpg"

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        canvas.height = 150
        canvas.width = 150
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
            // ctx.font = "40px Courier"
            // ctx.fillText(this.props.text, 210, 75)
          }
      }

    render() {

        function test(data) {
            for (let i = 0; i < data.length; i+= 4) {
              data[i] = data[i] ^ 255; // Invert Red
              data[i+1] = data[i+1] ^ 255; // Invert Green
              data[i+2] = data[i+2] ^ 255; // Invert Blue
            }
          }

          function swap(data) {
              let randomNum = Math.floor(Math.random()* 256)

              console.log(randomNum)
              for(let i = 0; i < data.length; i+= 4) {
                data[i] = randomNum

              }
          }



        // function test(data) {
        //    console.log(data)
        // }


       const drawNewImage = () => {

            const canvas = this.refs.canvas
            const ctx = canvas.getContext("2d")

            let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)
            ctx.putImageData(copyData, 0, 0)

            swap(copyData.data)
            console.log(copyData.data)

            ctx.putImageData(copyData,0 ,0 )
        }

        // const style = {
        //     height: "auto",
        //     width: "auto"
        // }

        const hidden = {
            display: "none"
        }

        return (
            <React.Fragment> 

                {/* <div className="container">
                    <img src={testImage} id="img"style={style}/>
                </div> */}

                <button onClick={drawNewImage}>
                    test
                </button>

                <div className="container">
                    <canvas ref="canvas"/> 
                    <img ref="image" src={testImage} style={hidden} />
                </div>

                <div> 

                </div>
            </React.Fragment>
        )
    }
}

export default CanvasApp
