import React, { Component } from 'react'
import testImage from "./test-images/150.jpg"

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.state = {
             imageHeight: null,
             imageWidth: null
        }
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        canvas.height = 200
        canvas.width = 200
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
          }
        this.setState({
            imageHeight:canvas.height,
            imageWidth:canvas.width
        })  
      }



    test = (data) => {
        const row = this.state.imageWidth
        const col = this.state.imageHeight 
        let arrArr = []

        for(let i = 0; i < data.length; i+=4) {
            let pixel = [data[i],data[i + 1],data[i + 2],data[i + 3]]
            arrArr.push(pixel)
        }

        // console.log(data)
        // console.log(arrArr)
        console.log(arrArr[0])
        console.log(arrArr[0][3])

        // for(let i = 0; i < row; i++) {
        //     for(let j = 0; j < row; j++) {
        //         if(arrArr[i][0])
        //     }
        // }
    }


    drawNewImage = () => {
        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")
        let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)
        ctx.putImageData(copyData, 0, 0)


        this.test(copyData.data)

        ctx.putImageData(copyData,0 ,0 )
    }


    render() {
        const width = this.state.imageWidth
        const height = this.state.imageHeight


        const hidden = {
            display: "none"
        }

        return (
            <React.Fragment> 

                {/* <div className="container">
                    <img src={testImage} id="img"style={style}/>
                </div> */}

                <button onClick={this.drawNewImage}>
                    test
                </button>

                <div className="container">
                    <canvas ref="canvas"/> 
                    <img ref="image" src={testImage} style={hidden} />
                </div>

                <div> 

                </div>
            </React.Fragment>
        )
    }
}

export default CanvasApp
