import React, { Component } from 'react'
import testImage from "./test-images/200.jpg"
import { arrowFunctionExpression } from '@babel/types'
import { sortByRed } from "./functions"

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.imageRef = React.createRef()
        this.canvasRef = React.createRef()
        this.state = {
             imageHeight: null,
             imageWidth: null
        }
    }

    componentDidMount() {
        const canvas = this.canvasRef.current
        const img = this.imageRef.current

        const ctx = canvas.getContext("2d")

        canvas.height = 200
        canvas.width = 200

        img.onload = () => {
            ctx.drawImage(img, 0, 0)

          }
        this.setState({
            imageHeight:canvas.height,
            imageWidth:canvas.width
        })  
      }

    drawNewImage = () => {
        const canvas = this.canvasRef.current
        const ctx = canvas.getContext("2d")
        let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)
        ctx.putImageData(copyData, 0, 0)

        copyData = sortByRed(copyData.data, this.state.imageHeight, this.state.imageWidth)



        ctx.putImageData(copyData,0 ,0 )
    }

    setOption = () => {
        console.log('test')
    }


    render() {

        const hiddenStyle = {
            display: "hidden"
        }

        const convertedStyle = {
            border: "1px solid red"
        }

        return (
            <React.Fragment> 

                <button onClick={this.drawNewImage}>
                    test
                </button>
                <select name="options" id="sort-options" onChange={this.setOption}>
                    <option value="one">Sort by red</option>
                    <option value="two">Sort by blue</option>
                    <option value="three">Sort by reen</option>
                    <option value="four">Sort by brightness</option>
                </select>

                <div className="container">

                        <p> Original </p>

                    <img ref={this.imageRef} src={testImage} style={hiddenStyle}/>

                        <p> to be converted </p>

                    <canvas ref={this.canvasRef}  style={convertedStyle}/> 
                </div>

                <div> 

                </div>
            </React.Fragment>
        )
    }
}


export default CanvasApp
