import React, { Component } from 'react'
import testImage from "./test-images/200.jpg"
import { arrowFunctionExpression } from '@babel/types'

export class CanvasAppCopy extends Component {
    constructor(props) {
        super(props)
        this.state = {
             imageHeight: null,
             imageWidth: null
        }
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        canvas.height = 200
        canvas.width = 200
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
          }
        this.setState({
            imageHeight:canvas.height,
            imageWidth:canvas.width
        })  
      }


    drawNewImage = () => {

        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")


        let imageData = ctx.getImageData(0,0, this.state.imageWidth, this.state.imageHeight)

        let len = imageData.data.length

        for(let i = 0; i < len - 4; i +=4)  {
            len[i] = Math.floor(Math.random() * 256)
            len[i+1] = Math.floor(Math.random() * 256)
            len[i+2] = Math.floor(Math.random() * 256)
            len[i+3] = Math.floor(Math.random() * 256)
        }

        ctx.putImageData(imageData, 0, 0)
        console.log(imageData.data)
        console.log('done')
    }


    render() {

        const width = this.state.imageWidth
        const height = this.state.imageHeight

        const hidden = {
            display: "none"
        }

        return (
            <React.Fragment> 

                {/* <div className="container">
                    <img src={testImage} id="img"style={style}/>
                </div> */}

                <button onClick={this.drawNewImage}>
                    test
                </button>

                <div className="container">

                        <p> Original </p>

                    <img ref="image" src={testImage}  />

                        <p> to be converted </p>

                    <canvas ref="canvas"/> 
                </div>

                <div> 

                </div>
            </React.Fragment>
        )
    }
}

export default CanvasAppCopy



