import React, { Component } from 'react'
import testImage from "./test-images/dog.jpg"

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }

    componentDidMount() {
        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")
        const img = this.refs.image

        img.onload = () => {
            ctx.drawImage(img, 0, 0)
            // ctx.font = "40px Courier"
            // ctx.fillText(this.props.text, 210, 75)
          }
      }






    render() {



       const drawNewImage = () => {
        const canvas = this.refs.canvas
        const ctx = canvas.getContext("2d")
        const img = this.refs.image
            let copyData = ctx.getImageData(0,0, canvas.width, canvas.height)
            ctx.putImageData(copyData, 0, 0)

            function changeToWhite(data) {
                for (var i = 0; i < data.length; i++) {
                  data[i] = 255;
                }
            }

            changeToWhite(copyData.data)

            ctx.putImageData(copyData,0 ,0 )
        }

        const style = {
            height: "auto",
            width: "auto"
        }

        const hidden = {
            display: "none"
        }

        return (
            <React.Fragment> 

                {/* <div className="container">
                    <img src={testImage} id="img"style={style}/>
                </div> */}

                <div className="container">
                    <canvas ref="canvas" height={window.innerHeight} width={window.innerWidth}/> 
                    <img ref="image" src={testImage} style={hidden} />
                </div>
                <button onClick={drawNewImage}>
                    test
                </button>
                <div> 


                </div>
            </React.Fragment>
        )
    }
}

export default CanvasApp
