import React, { Component } from 'react'
import imageToBase64 from 'image-to-base64'
import dog from './test-images/dog.jpg'

export class ConvertedImage extends Component {
    constructor(props) {
        super(props)

        this.state = {
             img: dog
        }
    }

    convert() {

        imageToBase64('./test-images/dog.jpg') // Path to the image
        .then(
            (response) => {
                console.log(response); // "cGF0aC90by9maWxlLmpwZw=="
            }
        )
        .catch(
            (error) => {
                console.log(error); // Logs an error if there was one
            }
        )
    }


    render() {
        const img = this.state.img
        return (
            <div>
                <button onClick={this.convert}> Convert to base 64</button>
                <div> 
                    <img src={img} />
                </div>
            </div>
        )
    }
}

export default ConvertedImage
