import React, { Component } from 'react'
// import imageToBase64 from 'image-to-base64'
import dog from './test-images/dog.jpg'
import FileBase64 from 'react-file-base64';

export class ConvertedImage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            img: dog,
            data: null
        }
    }


    getFiles(data){
        let arr = ''
        this.setState({ converted: data })

        arr = this.state.converted[0].base64
        console.log(arr)
      }

    render() {
        const img = this.state.img

        return (
            <div className="container">
                <button onClick={this.getFiles(img)}> Convert to base 64</button>
                <div className="image-container"> 
                    <FileBase64 
                    multiple={ true }
                    onDone={ this.getFiles.bind(this) } />
                </div>
            </div>
        )
    }
}

export default ConvertedImage
