import React, { Component } from 'react'
// import imageToBase64 from 'image-to-base64'
import dog from './test-images/dog.jpg'
import FileBase64 from 'react-file-base64';

export class ConvertedImage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            img: dog,
            data: null,
        }
    }


    getFiles(data){
        let imgString = ''
        let newImgArr = []

        this.setState({ converted: data })

        imgString = this.state.converted[0].base64

        for(let i = 0 ; i < imgString.length; i++){
            if(imgString[i] === 'B') {
                newImgArr.push('A')
            } else {
                newImgArr.push(imgString[i])
            }
        }

        let newImgString = newImgArr.join("")

        console.log(newImgString.slice(0, 500))
      }

    render() {
        const img = this.state.img

        return (
            <div className="container">
                <button onClick={this.getFiles}> Convert to base 64</button>
                <div className="image-container"> 
                    <FileBase64 
                    multiple={ true }
                    onDone={ this.getFiles.bind(this) } />
                </div>
            </div>
        )
    }
}

export default ConvertedImage
