import React, { Component } from 'react'
import testImage from "./test-images/dog.jpg"

export class CanvasApp extends Component {
    constructor(props) {
        super(props)
        this.state = {
            canvasWidth: null,
            canvasHeight: null
        }
    }

    componentWillMount() {
        this.setState({
            canvasWidth: 800,
            canvasHeight: 600
        })
      }



    componentDidMount() {
        const context = this.canvas.getContext('2d');
        const image = new Image();
        image.src = {testImage};
        image.onload = () => {
          context.drawImage(image, 0, 0, this.canvas.width, this.canvas.height);
        };
      }



    render() {

        const style = {
            height: "400px",
            width: "auto"
        }

        return (
            <React.Fragment> 
                {/* <div className="container">
                    <img src={testImage} id="img"style={style}/>
                </div> */}

                <div className="container">
                    <canvas id="canvas"> </canvas>
                </div>
            </React.Fragment>
        )
    }
}

export default CanvasApp
